frappe.listview_settings['Sales Order'] = {
    onload: function(listview) {
        // First check the system setting
        frappe.db.get_single_value('System Settings', 'autorisation_de_validation_quotidienne')
            .then(isEnabled => {
                // Only proceed if the setting is checked/enabled
                if (isEnabled) {
                    // Get user roles
                    frappe.call({
                        method: 'frappe.client.get',
                        args: {
                            doctype: 'User',
                            name: frappe.session.user
                        },
                        callback: function(response) {
                            if (response.message) {
                                const userRoles = response.message.roles || [];
                                const hasRole = userRoles.some(role => role.role === 'Sales Manager');
                                
                                if (hasRole) {
                                    let btn = listview.page.add_inner_button(__('Valider les commande du jour'), function() {
                                        // Show confirmation dialog before proceeding
                                        frappe.confirm(
                                            'Voulez-vous valider toutes les commandes du jour?',
                                            function() {
                                                // Show a loading indicator
                                                frappe.show_alert({
                                                    message: __('Validation des commandes en cours...'),
                                                    indicator: 'blue'
                                                });
                                                
                                                // Get current date in YYYY-MM-DD format
                                                let today = frappe.datetime.get_today();
                                                
                                                // Find all draft sales orders from today
                                                frappe.call({
                                                    method: 'frappe.client.get_list',
                                                    args: {
                                                        doctype: 'Sales Order',
                                                        filters: {
                                                            'docstatus': 0, // Draft status
                                                            'transaction_date': today
                                                        },
