                                                            let submitCount = 0;
                                                            let failedOrders = [];
                                                            let totalOrders = r.message.length;
                                                            
                                                            // Process each order
                                                            r.message.forEach(order => {
                                                                // First get the full document
                                                                frappe.call({
                                                                    method: 'frappe.client.get',
                                                                    args: {
