                                                                                    if (submit_r.exc) {
                                                                                        failedOrders.push({
                                                                                            name: order.name,
                                                                                            reason: submit_r.exc
                                                                                        });
                                                                                    } else {
                                                                                        submitCount++;
                                                                                    }
                                                                                    
                                                                                    // Check if all orders have been processed
                                                                                    if (submitCount + failedOrders.length === totalOrders) {
                                                                                        let message = '';
                                                                                        if (submitCount > 0) {
                                                                                            message += `${submitCount} commande(s) valid√©e(s) avec succ√®s.\n`;
                                                                                        }
                                                                                        if (failedOrders.length > 0) {
                                                                                            message += `\nCommandes non valid√©es:\n`;
                                                                                            failedOrders.forEach(failed => {
                                                                                                message += `- ${failed.name} (${failed.reason})\n`;
                                                                                            });
                                                                                        }
                                                                                        
                                                                                        frappe.show_alert({
                                                                                            message: message,
                                                                                            indicator: failedOrders.length === 0 ? 'green' : 'orange'
                                                                                        }, 20);
                                                                                        
                                                                                        // Refresh the list view
                                                                                        listview.refresh();
                                                                                    }
                                                                                }
                                                                            });
                                                                        } else {
                                                                            failedOrders.push({
                                                                                name: order.name,
                                                                                reason: "Could not fetch document"
                                                                            });
                                                                        }
                                                                    }
                                                                });
                                                            });
                                                        } else {
                                                            frappe.show_alert({
                                                                message: __('Il n\'y a pas de commandes pour aujourd\'hui.'),
                                                                indicator: 'blue'
                                                            });
                                                        }
                                                    }
                                                });
                                            }
                                        );
                                    });
                                    
                                      // Add the desired class to the button
