                    indicator: 'red'
                });
            });
    }
};

frappe.ui.form.on('Trajet de Livraison', {
    refresh: function(frm) {
        // Store user coordinates globally
        frm.user_coordinates = {
            latitude: null,
            longitude: null
        };
        
        
       // Define handleDeliveryReturn
      

        // Get user's location and then fetch delivery data
        if ("geolocation" in navigator) {
            navigator.geolocation.getCurrentPosition(
                function(position) {
                    frm.user_coordinates.latitude = position.coords.latitude;
                    frm.user_coordinates.longitude = position.coords.longitude;
                    console.log("Got user location:", frm.user_coordinates);
                    fetchDeliveryTripData();
                },
                function(error) {
                    console.log("Geolocation error:", error);
                    // Fallback coordinates (Algeria)
                    frm.user_coordinates.latitude = 35.6985752;
                    frm.user_coordinates.longitude = -0.6181506;
                    fetchDeliveryTripData();
                }
            );
        } else {
            // Fallback if geolocation is not available
            frm.user_coordinates.latitude = 35.6985752;
            frm.user_coordinates.longitude = -0.6181506;
            fetchDeliveryTripData();
        }

        const calculateDistance = (lat1, lon1, lat2, lon2) => {
            const R = 6371; // Earth's radius in km
            const dLat = (lat2 - lat1) * Math.PI / 180;
            const dLon = (lon2 - lon1) * Math.PI / 180;
            const a = 
                Math.sin(dLat/2) * Math.sin(dLat/2) +
                Math.cos(lat1 * Math.PI / 180) * Math.cos(lat2 * Math.PI / 180) * 
                Math.sin(dLon/2) * Math.sin(dLon/2);
            const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1-a));
            const distance = R * c;
            return Math.round(distance * 10) / 10;
        };

        const fetchDeliveryTripData = () => {
            frappe.call({
                method: 'frappe.client.get',
                args: {
                    doctype: 'Delivery Trip',
                    name: frm.doc.donnee_trajet
                },
                callback: function(response) {
                    if (response.message) {
                        const deliveryTrip = response.message;
                        fetchDeliveryNotes(deliveryTrip);
                    }
                }
            });
        };

        const formatCurrency = (amount) => {
            return frappe.format(amount, { fieldtype: 'Currency' });
        };

        const createDeliveryHeader = (tripData) => {
            const unvisitedStops = tripData.delivery_stops.filter(stop => !stop.visited);
            const totalStops = tripData.delivery_stops.length;
            const progress = ((totalStops - unvisitedStops.length) / totalStops) * 100;
        
            const headerContainer = document.getElementById('deliveryHeader');
            headerContainer.innerHTML = `
                <div class="delivery-header">
                    <div class="header-title">Trajet de Livraison: ${tripData.name}</div>
                    <div class="header-info">
                        <div class="header-info-item">
                            üë§ Chauffeur: ${tripData.nom_complet || 'Non assign√©'}
                        </div>
                        <div class="header-info-item">
                            üöõ V√©hicule: ${tripData.vehicle || 'Non assign√©'}
                        </div>
                        <div class="header-info-item">
                            üìÖ Date: ${tripData.departure_time || 'Non assign√©'}
                        </div>
                        <div class="header-info-item">
                            Status: ${tripData.status}
                        </div>
                    </div>
                    <div class="progress-bar">
                        <div class="progress-bar-fill" style="width: ${progress}%;"></div>
                    </div>
                </div>
            `;
        };

        const fetchDeliveryNotes = (tripData) => {
            const promises = tripData.delivery_stops.map(stop => {
                if (stop.delivery_note) {
                    return frappe.call({
                        method: 'frappe.client.get',
                        args: {
                            doctype: 'Delivery Note',
                            name: stop.delivery_note
                        }
                    });
                }
                return Promise.resolve({ message: null });
            });

            Promise.all(promises).then(responses => {
                const deliveryNotes = responses
                    .map(r => r.message)
                    .filter(note => note !== null);
                
                const customerPromises = deliveryNotes.map(note => {
                    return frappe.call({
                        method: 'frappe.client.get',
                        args: {
                            doctype: 'Customer',
                            name: note.customer
                        }
                    });
                });

                Promise.all(customerPromises).then(customerResponses => {
                    const customerInfo = customerResponses.map(r => r.message);
                    displayDeliveryInfo(tripData, deliveryNotes, customerInfo);
                });
            });
        };

        const createDeliveryCard = (note, customerInfo, tripData) => {
            const deliveryStop = tripData.delivery_stops.find(stop => 
                stop.delivery_note === note.name
            );
            const isDelivered = deliveryStop && deliveryStop.visited;

            // Calculate distance
            const distance = calculateDistance(
                frm.user_coordinates.latitude,
                frm.user_coordinates.longitude,
                parseFloat(customerInfo.geo_latitude) || 0,
                parseFloat(customerInfo.geo_longitude) || 0
            );

            const card = document.createElement('div');
            card.className = `delivery-card ${isDelivered ? 'delivered' : ''}`;
            
            card.innerHTML = `
                <div class="distance-badge" style="background-color: ${distance > 10 ? '#FF4444' : '#4CAF50'}">
                    ${distance} km
                </div>
                <div class="customer-name">
                    ${note.customer_name}
                    ${isDelivered ? '<span class="delivered-tag">‚úì Livr√©e</span>' : ''}
                </div>
                <div class="delivery-info">Bon de livraison: ${note.name}</div>
                <div class="delivery-info">N¬∞ Client: ${customerInfo.name || 'N/A'}</div>
                ${!isDelivered ? '<button class="map-button"><i class="fa fa-map-marker"></i> Directions</button>' : ''}
            `;

            if (!isDelivered) {
                card.addEventListener('click', () => showDeliveryModal(note, customerInfo, tripData));
                
                const mapButton = card.querySelector('.map-button');
                if (mapButton) {
                    mapButton.addEventListener('click', (e) => {
                        e.stopPropagation();
                        openDirections(
                            frm.user_coordinates.latitude,
                            frm.user_coordinates.longitude,
                            customerInfo.geo_latitude,
                            customerInfo.geo_longitude
                        );
                    });
                }
            }
            
            return card;
        };

