frappe.ui.form.on('Trajet de Livraison', {
    refresh: function(frm) {
        // Store user coordinates globally
        frm.user_coordinates = {
            latitude: null,
            longitude: null
        };
        
        
       // Define handleDeliveryReturn
      

        // Get user's location and then fetch delivery data
        if ("geolocation" in navigator) {
            navigator.geolocation.getCurrentPosition(
                function(position) {
                    frm.user_coordinates.latitude = position.coords.latitude;
                    frm.user_coordinates.longitude = position.coords.longitude;
                    console.log("Got user location:", frm.user_coordinates);
                    fetchDeliveryTripData();
                },
                function(error) {
                    console.log("Geolocation error:", error);
                    // Fallback coordinates (Algeria)
                    frm.user_coordinates.latitude = 35.6985752;
                    frm.user_coordinates.longitude = -0.6181506;
                    fetchDeliveryTripData();
                }
            );
        } else {
            // Fallback if geolocation is not available
            frm.user_coordinates.latitude = 35.6985752;
            frm.user_coordinates.longitude = -0.6181506;
            fetchDeliveryTripData();
        }

        const calculateDistance = (lat1, lon1, lat2, lon2) => {
            const R = 6371; // Earth's radius in km
            const dLat = (lat2 - lat1) * Math.PI / 180;
            const dLon = (lon2 - lon1) * Math.PI / 180;
            const a = 
                Math.sin(dLat/2) * Math.sin(dLat/2) +
                Math.cos(lat1 * Math.PI / 180) * Math.cos(lat2 * Math.PI / 180) * 
                Math.sin(dLon/2) * Math.sin(dLon/2);
            const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1-a));
            const distance = R * c;
            return Math.round(distance * 10) / 10;
        };

        const fetchDeliveryTripData = () => {
            frappe.call({
                method: 'frappe.client.get',
                args: {
                    doctype: 'Delivery Trip',
                    name: frm.doc.donnee_trajet
                },
                callback: function(response) {
                    if (response.message) {
                        const deliveryTrip = response.message;
                        fetchDeliveryNotes(deliveryTrip);
                    }
                }
            });
        };

        const formatCurrency = (amount) => {
            return frappe.format(amount, { fieldtype: 'Currency' });
        };

